function getStatistics(){var n={};n.Multiplier=$("#Multiplier").val();n.StrikePrice=$("#StrikePrice").val();n.InitialSL=$("#InitialSl").val();n.InitialTP=$("#InitialTp").val();n.PricePerUnit=$("#PricePerUnit").val();n.OrderCosts=$("#OrderCosts").val();n.Units=$("#Units").val();n.WKN=$("#Wkn").val();n.IsLong=document.getElementById("IsLong").checked;$.ajax({url:"/Calculation/GetStatistics",data:n,dataType:"json",type:"POST",error:function(){alert("An error occurred.")},success:function(n){document.getElementById("CalculationResults").innerHTML=n.Result}})}function getPriceFromUnderlying(n,t,i,r){$.ajax({url:"/Calculation/CalculatePriceFromUnderlying",data:{underlyingPrice:n,multiplier:t,strikePrice:i,isLong:r},dataType:"json",type:"POST",error:function(){alert("An error occurred.")},success:function(n){document.getElementById("UnderlyingResult").innerHTML=n}})}$(function(){$("#Multiplier").keyup(function(){getStatistics()})});$(function(){$("#StrikePrice").keyup(function(){getStatistics()})});$(function(){$("#InitialSl").keyup(function(){getStatistics()})});$(function(){$("#InitialTp").keyup(function(){getStatistics()})});$(function(){$("#PricePerUnit").keyup(function(){getStatistics()})});$(function(){$("#OrderCosts").keyup(function(){getStatistics()})});$(function(){$("#Units").keyup(function(){getStatistics()})});$(function(){$("#IsLong").change(function(){getStatistics()})});$(function(){$("#UnderlyingCalc").keyup(function(){getPriceFromUnderlying($("#UnderlyingCalc").val(),$("#Multiplier").val(),$("#StrikePrice").val(),document.getElementById("IsLong").checked)})});